(function($) {

  $.fn.date = function() {

    return this.each(function() {

      var input  = $(this).attr('type', 'text');
      var format = input.data('format');

      input.attr('placeholder', format);

      new Pikaday({
        field    : this,
        firstDay : 1,
        format   : format,
        i18n     : input.data('i18n')
      });

    });

  };

})(jQuery);